create table address (id bigint generated by default as identity, city varchar(255), state varchar(255), street varchar(255), type varchar(255), zip varchar(255), primary key (id));
create table club (address_id bigint unique, id bigint generated by default as identity, level_id bigint unique, management_id bigint unique, abbreviation varchar(255), email varchar(255), logo_url varchar(255), name varchar(255), phone_number varchar(255), website varchar(255), primary key (id));
create table level_entity (id bigint generated by default as identity, level_name varchar(255), primary key (id));
create table management (id bigint generated by default as identity, contact varchar(255), name varchar(255), primary key (id));
create table stadium (address_id bigint unique, club_id bigint, id bigint generated by default as identity, name varchar(255), type varchar(255), primary key (id));
alter table if exists club add constraint fk_club_address foreign key (address_id) references address;
alter table if exists club add constraint fk_club_level foreign key (level_id) references level_entity;
alter table if exists club add constraint fk_club_management foreign key (management_id) references management;
alter table if exists stadium add constraint fk_stadium_address foreign key (address_id) references address;
alter table if exists stadium add constraint fk_stadium_club foreign key (club_id) references club;
